// Combine all analysis results into final verdict
const combineAnalysisResults = (analyses, content, user) => {
  const { contentAnalysis, urlAnalysis, languageAnalysis, factCheck } = analyses;
  
  // Calculate overall scores
  const scamRisk = Math.round((contentAnalysis.scamScore + urlAnalysis.urlRisk + languageAnalysis.urgencyScore) / 3);
  const credibilityScore = Math.round(100 - ((contentAnalysis.misinfoScore + scamRisk + (100 - factCheck.factCheckScore)) / 3));
  
  // Determine verdict
  let verdict = 'Credible';
  if (credibilityScore < 40 || scamRisk > 70) verdict = 'High Risk';
  else if (credibilityScore < 70 || scamRisk > 40) verdict = 'Suspicious';
  
  // Compile warnings
  const warnings = [
    ...contentAnalysis.patterns,
    ...urlAnalysis.warnings
  ];
  
  // Generate recommendations
  const recommendations = [];
  if (scamRisk > 50) {
    recommendations.push('Do not click any links or provide personal information');
    recommendations.push('Verify sender through official channels');
  }
  if (credibilityScore < 60) {
    recommendations.push('Cross-check information with reliable sources');
    recommendations.push('Look for supporting evidence from multiple sources');
  }
  if (urlAnalysis.urlCount > 0) {
    recommendations.push('Hover over links to preview destinations before clicking');
  }
  
  recommendations.push('Report suspicious content to help protect others');
  
  return {
    credibilityScore,
    scamRisk,
    verdict,
    warnings,
    recommendations,
    summary: `Based on AI analysis of language patterns, content structure, and ${factCheck.sources} fact-checking sources, this content shows ${scamRisk}% scam risk indicators and ${credibilityScore}% credibility. Analysis performed for ${user.name}.`,
    analysisDate: new Date().toLocaleString(),
    confidence: Math.round((credibilityScore + (100 - scamRisk)) / 2)
  };
};

