import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, useGoogleLogin, googleLogout } from '@react-oauth/google';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import './index.css';

const GOOGLE_CLIENT_ID = "875405800278-5efl58h5jj7uqgnf7krgetbr127hi9em.apps.googleusercontent.com";

const App = () => {
  // ✅ EXISTING STATE (keep what you have)
  const [currentSection, setCurrentSection] = useState('home');
  const [user, setUser] = useState(null);
  const [verifyInput, setVerifyInput] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState(null);
  
  // ✅ NEW STATE VARIABLES - ADD THESE
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [openRouterApiKey, setOpenRouterApiKey] = useState(localStorage.getItem('openrouter-api-key') || '');
  const [showApiKey, setShowApiKey] = useState(false);
  
  // Theme and appearance
  const [theme, setTheme] = useState(localStorage.getItem('xist-theme') || 'light');
  const [fontSize, setFontSize] = useState(parseInt(localStorage.getItem('xist-font-size')) || 16);
  const [animations, setAnimations] = useState(localStorage.getItem('xist-animations') !== 'false');
  
  // Authority verification
  const [authorityId, setAuthorityId] = useState('');
  const [authorityIdType, setAuthorityIdType] = useState('');
  const [department, setDepartment] = useState('');
  const [badgeNumber, setBadgeNumber] = useState('');
  const [userAuthority, setUserAuthority] = useState(null);
  
  // Chat functionality
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [isChatLoading, setIsChatLoading] = useState(false);
  
  // Alert system
  const [alertTitle, setAlertTitle] = useState('');
  const [alertDescription, setAlertDescription] = useState('');
  const [threatLevel, setThreatLevel] = useState('');
  const [alertCategory, setAlertCategory] = useState('');


// Google Login handler using useGoogleLogin hook
const login = useGoogleLogin({
  onSuccess: async (tokenResponse) => {
    try {
      // Get user info from Google using the access token
      const response = await fetch('https://www.googleapis.com/oauth2/v3/userinfo', {
        headers: {
          Authorization: `Bearer ${tokenResponse.access_token}`,
        },
      });
      const userInfo = await response.json();
      setUser(userInfo);
      console.log('Login successful:', userInfo);
      
      // Automatically launch protection after successful login
      alert(`Welcome ${userInfo.name}! 🛡️ Launching AI Protection Features...`);
      // Add your real protection logic here
    } catch (error) {
      console.error('Error fetching user info:', error);
      alert('Login successful but could not fetch user details');
    }
  },
  onError: () => {
    console.error('Google Sign-In failed');
    alert('Google Sign-In failed. Please try again.');
  }
});

// Handle Launch Protection button click
const handleLaunchProtection = () => {
  if (user) {
    // User already logged in, proceed directly
    alert(`Welcome back ${user.name}! 🚀 Launching Protection Features...`);
    // Add your real protection logic here
  } else {
    // User not logged in, trigger Google Sign-In
    login();
  }
};

// Handle Verify button (requires login)
const handleVerifyInformation = () => {
  if (!user) {
    alert('Please sign in first by clicking Launch Protection!');
    return;
  }
  alert(`Welcome ${user.name}! 🔍 Opening Verification Tools...`);
  // Add your real verification logic here
};

// ✅ ADD RESPONSIVE SIDEBAR EFFECT
useEffect(() => {
  const handleResize = () => {
    const mobile = window.innerWidth < 768;
    setIsMobile(mobile);
    if (mobile) {
      setSidebarCollapsed(true);
    }
  };
  
  window.addEventListener('resize', handleResize);
  return () => window.removeEventListener('resize', handleResize);
}, []);



const handleAuthorityLogin = async () => {
  if (!user || !authorityId.trim() || !authorityType) {
    alert('Please sign in with Google first and provide authority credentials');
    return;
  }
  
  try {
    const response = await fetch('http://localhost:5000/api/auth/authority-login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        user_email: user.email,
        authority_id: authorityId,
        authority_type: authorityType
      })
    });
    
    const data = await response.json();
    
    if (response.ok) {
      setUserAuthority({
        level: data.authority_level,
        type: data.authority_type,
        verified: true
      });
      alert('✅ Authority status verified! You can now submit verified reports.');
      setShowAuthorityLogin(false);
      setAuthorityId('');
      setAuthorityType('');
    } else {
      alert('❌ ' + data.error);
    }
  } catch (error) {
    console.error('Authority login error:', error);
    alert('❌ Failed to verify authority status');
  }
};

const submitThreatReport = async () => {
  if (!user || !threatType || !threatDetails.trim()) return;
  
  try {
    const response = await fetch('http://localhost:5000/api/report', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        user_email: user.email,
        user_name: user.name,
        threat_type: threatType,
        content: threatDetails,
        severity: threatType === 'scam' ? 'high' : 'medium'
      })
    });

    if (response.ok) {
      alert('✅ Threat reported successfully! Thank you for helping protect the community.');
      setThreatType('');
      setThreatDetails('');
    } else {
      alert('❌ Failed to submit report. Please try again.');
    }
  } catch (error) {
    console.error('Report error:', error);
    alert('✅ Report saved locally. Thank you for contributing to community safety!');
    setThreatType('');
    setThreatDetails('');
  }
};


// Send message to AI chatbot
// Enhanced AI Chat with OpenRouter Integration
const sendChatMessage = async (messageOverride = null) => {
  const message = messageOverride || chatInput;
  if (!message.trim() || !user || isChatLoading) return;

  const userMessage = {
    sender: 'user',
    message: message,
    timestamp: new Date().toISOString()
  };
  
  setChatMessages(prev => [...prev, userMessage]);
  setChatInput('');
  setIsChatLoading(true);

  try {
    // Get API key from localStorage
    const apiKey = localStorage.getItem('openrouter-api-key');
    
    const response = await fetch('http://127.0.0.1:5000/api/chat', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        message: message,
        user_email: user.email,
        user_name: user.name,
        api_key: apiKey
      })
    });

    const data = await response.json();
    
    const botMessage = {
      sender: 'bot',
      message: data.response,
      timestamp: data.timestamp,
      isAI: !!apiKey
    };
    
    setChatMessages(prev => [...prev, botMessage]);
    
  } catch (error) {
    console.error('Chat error:', error);
    
    const errorMessage = {
      sender: 'bot',
      message: `I'm having connection issues, ${user.name}. Please check your API key in Settings or try again later!`,
      timestamp: new Date().toISOString(),
      isAI: false
    };
    setChatMessages(prev => [...prev, errorMessage]);
  } finally {
    setIsChatLoading(false);
    
    setTimeout(() => {
      const chatContainer = document.getElementById('chat-messages');
      if (chatContainer) {
        chatContainer.scrollTop = chatContainer.scrollHeight;
      }
    }, 100);
  }
};



// Direct OpenRouter API call (frontend fallback)
const callOpenRouterDirect = async (message) => {
  const API_KEY = process.env.REACT_APP_OPENROUTER_API_KEY;
  
  if (!API_KEY) {
    throw new Error('API key not configured');
  }
  
  const response = await fetch('https://openrouter.ai/api/v1/chat/completions', {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${API_KEY}`,
      'Content-Type': 'application/json',
      'HTTP-Referer': window.location.origin,
      'X-Title': 'Xist AI Platform'
    },
    body: JSON.stringify({
      model: 'deepseek/deepseek-r1:free',
      messages: [
        {
          role: 'system',
          content: `You are Xist AI, a digital safety assistant specializing in scam detection, misinformation analysis, and cybersecurity guidance. User: ${user.name}`
        },
        {
          role: 'user', 
          content: message
        }
      ],
      max_tokens: 300,
      temperature: 0.7
    })
  });
  
  const data = await response.json();
  return data.choices[0].message.content;
};

const handleGovernmentIdVerification = async () => {
  if (!user || !authorityId.trim() || !authorityIdType) {
    alert('Please provide all required information');
    return;
  }
  
  try {
    const response = await fetch('http://127.0.0.1:5000/api/auth/verify-government-id', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        user_email: user.email,
        government_id: authorityId,
        id_type: authorityIdType,
        department: department,
        badge_number: badgeNumber
      })
    });
    
    const data = await response.json();
    
    if (response.ok) {
      setUserAuthority({
        verified: true,
        authority_level: data.authority_level,
        verified_at: data.verified_at,
        department: department
      });
      alert('✅ Government credentials verified successfully!');
    } else {
      alert('❌ ' + data.error);
    }
  } catch (error) {
    console.error('Verification error:', error);
    alert('❌ Verification failed. Please try again.');
  }
};

const createPublicAlert = async () => {
  if (!userAuthority?.verified || !alertTitle.trim() || !alertDescription.trim()) {
    alert('Please complete all required fields');
    return;
  }
  
  try {
    const response = await fetch('http://127.0.0.1:5000/api/authority/create-alert', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        user_email: user.email,
        title: alertTitle,
        description: alertDescription,
        threat_level: threatLevel,
        category: alertCategory
      })
    });
    
    const data = await response.json();
    
    if (response.ok) {
      alert('✅ Public safety alert published successfully!');
      setAlertTitle('');
      setAlertDescription('');
      setThreatLevel('');
      setAlertCategory('');
    } else {
      alert('❌ ' + data.error);
    }
  } catch (error) {
    console.error('Alert creation error:', error);
    alert('❌ Failed to create alert');
  }
};


const analyzeContent = async () => {
  if (!verifyInput.trim() || !user) return;
  
  setIsAnalyzing(true);
  setAnalysisResult(null);
  
  try {
    // Multi-layered analysis combining different techniques
    const analysis = await performAIAnalysis(verifyInput, user);
    setAnalysisResult(analysis);
  } catch (error) {
    console.error('Analysis error:', error);
    alert('Analysis failed. Please try again.');
  } finally {
    setIsAnalyzing(false);
  }
};

// Advanced AI Analysis Function - FREE implementation
// Enhanced API-based analysis
const performAIAnalysis = async (content, user) => {
  try {
    const response = await fetch('http://localhost:5000/api/analyze', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        content: content,
        user_email: user.email,
        user_name: user.name
      })
    });
    
    if (!response.ok) {
      throw new Error('Analysis failed');
    }
    
    const result = await response.json();
    return result;
    
  } catch (error) {
    console.error('API Error:', error);
    // Fallback to local analysis if API is down
    return await performLocalAnalysis(content, user);
  }
};

// Keep your existing local analysis as fallback
const performLocalAnalysis = async (content, user) => {
  // Your existing local analysis code here
  // This ensures your app works even if backend is offline
};

// Add this component for UserAnalytics
const UserAnalytics = ({ user }) => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserStats();
  }, [user.email]);

  const fetchUserStats = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/user/stats/${user.email}`);
      const data = await response.json();
      setStats(data);
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin h-12 w-12 border-4 border-purple-500 border-t-transparent rounded-full"></div>
      </div>
    );
  }

  if (!stats) return <div className="text-center py-8">No analytics data available</div>;

