// Content Pattern Analysis (Detects scam indicators)
const analyzeContentPatterns = (content) => {
  const scamPatterns = [
    /urgent.*action.*required/i,
    /click.*here.*immediately/i,
    /you.*won.*\$[\d,]+/i,
    /limited.*time.*offer/i,
    /verify.*account.*suspended/i,
    /congratulations.*winner/i,
    /act.*now.*expires/i
  ];
  
  const suspiciousPhrases = [
    /miracle.*cure/i,
    /doctors.*hate.*this/i,
    /100%.*guaranteed/i,
    /secret.*government/i,
    /they.*don't.*want.*you.*to.*know/i
  ];
  
  let scamScore = 0;
  let misinfoScore = 0;
  let detectedPatterns = [];
  
  scamPatterns.forEach(pattern => {
    if (pattern.test(content)) {
      scamScore += 25;
      detectedPatterns.push('Scam language pattern detected');
    }
  });
  
  suspiciousPhrases.forEach(pattern => {
    if (pattern.test(content)) {
      misinfoScore += 20;
      detectedPatterns.push('Suspicious claim pattern detected');
    }
  });
  
  return {
    scamScore: Math.min(scamScore, 100),
    misinfoScore: Math.min(misinfoScore, 100),
    patterns: detectedPatterns
  };
};

// URL Analysis Function
const analyzeUrls = (content) => {
  const urlRegex = /https?:\/\/[^\s]+/g;
  const urls = content.match(urlRegex) || [];
  
  let urlRisk = 0;
  let urlWarnings = [];
  
  urls.forEach(url => {
    // Check for suspicious URL patterns
    if (url.includes('bit.ly') || url.includes('tinyurl') || url.includes('t.co')) {
      urlRisk += 30;
      urlWarnings.push('Shortened URL detected - could hide destination');
    }
    
    if (url.includes('http://') && !url.includes('https://')) {
      urlRisk += 20;
      urlWarnings.push('Unsecured HTTP connection detected');
    }
    
    // Check for suspicious domains
    const suspiciousDomains = ['free-money', 'win-big', 'urgent-action', 'verify-now'];
    if (suspiciousDomains.some(domain => url.includes(domain))) {
      urlRisk += 40;
      urlWarnings.push('Suspicious domain pattern detected');
    }
  });
  
  return {
    urlRisk: Math.min(urlRisk, 100),
    warnings: urlWarnings,
    urlCount: urls.length
  };
};

// Language Pattern Analysis
const analyzeLanguagePatterns = (content) => {
  const urgencyWords = ['urgent', 'immediately', 'expires', 'limited', 'hurry', 'act now'];
  const emotionWords = ['amazing', 'incredible', 'shocking', 'unbelievable', 'miracle'];
  
  const words = content.toLowerCase().split(/\s+/);
  let urgencyScore = 0;
  let emotionScore = 0;
  
  words.forEach(word => {
    if (urgencyWords.some(uw => word.includes(uw))) urgencyScore += 10;
    if (emotionWords.some(ew => word.includes(ew))) emotionScore += 5;
  });
  
  return {
    urgencyScore: Math.min(urgencyScore, 100),
    emotionScore: Math.min(emotionScore, 100),
    wordCount: words.length
  };
};

