import React, { useState, useEffect } from 'react';

const App = () => {
  const [currentSection, setCurrentSection] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (!isMobile) setSidebarOpen(false);
  }, [isMobile]);

  const menuItems = [
    { id: 'home', label: 'Home', icon: '🏠' },
    { id: 'verify', label: 'Verify Information', icon: '🛡️' },
    { id: 'education', label: 'Education', icon: '🎓' },
    { id: 'community', label: 'Community', icon: '👥' },
    { id: 'protection', label: 'Protection', icon: '🔒' },
    { id: 'analytics', label: 'Analytics', icon: '📊' },
    { id: 'authority', label: 'Authority', icon: '⚡' },
    { id: 'settings', label: 'Settings', icon: '⚙️' }
  ];

  const renderSection = () => {
    switch (currentSection) {
      case 'home':
        return (
          <div className="space-y-8">
            <div className="bg-gradient-to-br from-primary-500 to-primary-700 rounded-2xl p-8 text-white text-center">
              <div className="text-6xl mb-4">🧠</div>
              <h1 className="text-4xl font-bold mb-4">Welcome to VANTA AI</h1>
              <p className="text-xl opacity-90 mb-6">Advanced AI-driven platform for security and misinformation detection</p>
              <button className="bg-white text-primary-700 px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-all duration-200">
                Get Started
              </button>
            </div>
            
            <div className="grid md:grid-cols-3 gap-6">
              {[
                { icon: '🛡️', title: 'Advanced Security', desc: 'Real-time threat detection and analysis' },
                { icon: '🧠', title: 'AI Processing', desc: 'Intelligent information processing' },
                { icon: '📊', title: 'Smart Analytics', desc: 'Comprehensive data insights' }
              ].map((feature, index) => (
                <div key={index} className="bg-white rounded-xl p-6 shadow-sm border border-gray-200 text-center">
                  <div className="text-4xl mb-4">{feature.icon}</div>
                  <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                  <p className="text-gray-600">{feature.desc}</p>
                </div>
              ))}
            </div>
          </div>
        );

      case 'verify':
        return (
          <div className="max-w-4xl mx-auto space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-4">🔍</div>
              <h1 className="text-3xl font-bold text-gray-900 mb-4">Information Verification</h1>
              <p className="text-xl text-gray-600">Use AI to verify information credibility</p>
            </div>
            
            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
              <textarea
                placeholder="Enter text to verify..."
                rows="6"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 resize-none"
              />
              <button className="mt-4 bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-semibold">
                Verify Information
              </button>
            </div>
          </div>
        );

      case 'education':
        return (
          <div className="space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-4">🎓</div>
              <h1 className="text-3xl font-bold text-gray-900 mb-4">Education & Training</h1>
              <p className="text-xl text-gray-600">Enhance your AI security knowledge</p>
            </div>
            
            <div className="grid md:grid-cols-2 gap-6">
              {[
                { title: 'AI Security Fundamentals', desc: 'Learn AI-powered security basics', duration: '4 hours' },
                { title: 'Threat Detection Methods', desc: 'Advanced detection techniques', duration: '6 hours' }
              ].map((course, index) => (
                <div key={index} className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
                  <h3 className="text-xl font-bold mb-2">{course.title}</h3>
                  <p className="text-gray-600 mb-4">{course.desc}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-500">{course.duration}</span>
                    <button className="bg-primary-600 text-white px-4 py-2 rounded">Start Course</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      default:
        return (
          <div className="text-center py-16">
            <div className="text-6xl mb-4">🚧</div>
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Coming Soon</h2>
            <p className="text-gray-600">This section is under development</p>
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-gradient-to-r from-primary-600 to-primary-700 shadow-lg sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <div className="text-2xl">🧠</div>
              <h1 className="text-xl font-bold text-white">Xist AI</h1>
            </div>

            {isMobile && (
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                className="p-2 rounded-md text-white hover:bg-primary-700"
              >
                <div className="text-2xl">{sidebarOpen ? '✕' : '☰'}</div>
              </button>
            )}

            {!isMobile && (
              <nav className="flex space-x-6">
                {menuItems.slice(0, 4).map((item) => (
                  <button
                    key={item.id}
                    onClick={() => setCurrentSection(item.id)}
                    className={`text-white hover:text-primary-200 px-3 py-2 rounded-md text-sm font-medium ${
                      currentSection === item.id ? 'bg-primary-700' : ''
                    }`}
                  >
                    {item.label}
                  </button>
                ))}
              </nav>
            )}
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Mobile Sidebar */}
        {isMobile && sidebarOpen && (
          <>
            <div
              className="fixed inset-0 bg-black bg-opacity-50 z-40"
              onClick={() => setSidebarOpen(false)}
            />
            <aside className="fixed left-0 top-16 h-screen w-80 bg-white shadow-xl z-50 overflow-y-auto">
              <div className="p-6">
                <nav className="space-y-2">
                  {menuItems.map((item) => (
                    <button
                      key={item.id}
                      onClick={() => {
                        setCurrentSection(item.id);
                        setSidebarOpen(false);
                      }}
                      className={`w-full flex items-center space-x-3 px-4 py-3 text-left rounded-lg transition-all duration-200 ${
                        currentSection === item.id
                          ? 'bg-primary-50 text-primary-700'
                          : 'text-gray-700 hover:bg-gray-100'
                      }`}
                    >
                      <span className="text-xl">{item.icon}</span>
                      <span className="font-medium">{item.label}</span>
                    </button>
                  ))}
                </nav>
              </div>
            </aside>
          </>
        )}

        {/* Desktop Sidebar */}
        {!isMobile && (
          <aside className="w-80 bg-white shadow-lg h-screen overflow-y-auto border-r border-gray-200 fixed left-0 top-16">
            <div className="p-6">
              <nav className="space-y-2">
                {menuItems.map((item) => (
                  <button
                    key={item.id}
                    onClick={() => setCurrentSection(item.id)}
                    className={`w-full flex items-center space-x-3 px-4 py-3 text-left rounded-lg transition-all duration-200 ${
                      currentSection === item.id
                        ? 'bg-primary-50 text-primary-700'
                        : 'text-gray-700 hover:bg-gray-100'
                    }`}
                  >
                    <span className="text-xl">{item.icon}</span>
                    <span className="font-medium">{item.label}</span>
                  </button>
                ))}
              </nav>
            </div>
          </aside>
        )}

        {/* Main Content */}
        <main className={`flex-1 ${!isMobile ? 'ml-80' : ''} p-4 sm:p-6 lg:p-8`}>
          <div className="max-w-7xl mx-auto">
            {renderSection()}
          </div>
        </main>
      </div>
    </div>
  );
};

export default App;
