import React, { useState, useEffect } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useGoogleLogin, googleLogout } from '@react-oauth/google'; 

const App = () => {
  const [user, setUser] = useState(null);

// Google Login handler using useGoogleLogin hook
const login = useGoogleLogin({
  onSuccess: async (tokenResponse) => {
    try {
      // Get user info from Google using the access token
      const response = await fetch('https://www.googleapis.com/oauth2/v3/userinfo', {
        headers: {
          Authorization: `Bearer ${tokenResponse.access_token}`,
        },
      });
      const userInfo = await response.json();
      setUser(userInfo);
      console.log('Login successful:', userInfo);
      
      // Automatically launch protection after successful login
      alert(`Welcome ${userInfo.name}! 🛡️ Launching AI Protection Features...`);
      // Add your real protection logic here
    } catch (error) {
      console.error('Error fetching user info:', error);
      alert('Login successful but could not fetch user details');
    }
  },
  onError: () => {
    console.error('Google Sign-In failed');
    alert('Google Sign-In failed. Please try again.');
  }
});

// Handle Launch Protection button click
const handleLaunchProtection = () => {
  if (user) {
    // User already logged in, proceed directly
    alert(`Welcome back ${user.name}! 🚀 Launching Protection Features...`);
    // Add your real protection logic here
  } else {
    // User not logged in, trigger Google Sign-In
    login();
  }
};

// Handle Verify button (requires login)
const handleVerifyInformation = () => {
  if (!user) {
    alert('Please sign in first by clicking Launch Protection!');
    return;
  }
  alert(`Welcome ${user.name}! 🔍 Opening Verification Tools...`);
  // Add your real verification logic here
};
  const [currentSection, setCurrentSection] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [verifyInput, setVerifyInput] = useState('');
const [isAnalyzing, setIsAnalyzing] = useState(false);
const [analysisResult, setAnalysisResult] = useState(null);

const analyzeContent = async () => {
  if (!verifyInput.trim() || !user) return;
  
  setIsAnalyzing(true);
  setAnalysisResult(null);
  
  try {
    // Multi-layered analysis combining different techniques
    const analysis = await performAIAnalysis(verifyInput, user);
    setAnalysisResult(analysis);
  } catch (error) {
    console.error('Analysis error:', error);
    alert('Analysis failed. Please try again.');
  } finally {
    setIsAnalyzing(false);
  }
};

// Advanced AI Analysis Function - FREE implementation
const performAIAnalysis = async (content, user) => {
  // Simulate real AI analysis with multiple detection layers
  await new Promise(resolve => setTimeout(resolve, 3000)); // Realistic delay
  
  const analysisLayers = {
    // Layer 1: Content Pattern Analysis
    contentAnalysis: analyzeContentPatterns(content),
    
    // Layer 2: URL/Link Analysis (if URLs detected)
    urlAnalysis: analyzeUrls(content),
    
    // Layer 3: Language/Sentiment Analysis
    languageAnalysis: analyzeLanguagePatterns(content),
    
    // Layer 4: Fact-checking (using free APIs)
    factCheck: await performFactCheck(content)
  };
  
  // Combine all analyses into final verdict
  return combineAnalysisResults(analysisLayers, content, user);
};

// Content Pattern Analysis (Detects scam indicators)
const analyzeContentPatterns = (content) => {
  const scamPatterns = [
    /urgent.*action.*required/i,
    /click.*here.*immediately/i,
    /you.*won.*\$[\d,]+/i,
    /limited.*time.*offer/i,
    /verify.*account.*suspended/i,
    /congratulations.*winner/i,
    /act.*now.*expires/i
  ];
  
  const suspiciousPhrases = [
    /miracle.*cure/i,
    /doctors.*hate.*this/i,
    /100%.*guaranteed/i,
    /secret.*government/i,
    /they.*don't.*want.*you.*to.*know/i
  ];
  
  let scamScore = 0;
  let misinfoScore = 0;
  let detectedPatterns = [];
  
  scamPatterns.forEach(pattern => {
    if (pattern.test(content)) {
      scamScore += 25;
      detectedPatterns.push('Scam language pattern detected');
    }
  });
  
  suspiciousPhrases.forEach(pattern => {
    if (pattern.test(content)) {
      misinfoScore += 20;
      detectedPatterns.push('Suspicious claim pattern detected');
    }
  });
  
  return {
    scamScore: Math.min(scamScore, 100),
    misinfoScore: Math.min(misinfoScore, 100),
    patterns: detectedPatterns
  };
};

// URL Analysis Function
const analyzeUrls = (content) => {
  const urlRegex = /https?:\/\/[^\s]+/g;
  const urls = content.match(urlRegex) || [];
  
  let urlRisk = 0;
  let urlWarnings = [];
  
  urls.forEach(url => {
    // Check for suspicious URL patterns
    if (url.includes('bit.ly') || url.includes('tinyurl') || url.includes('t.co')) {
      urlRisk += 30;
      urlWarnings.push('Shortened URL detected - could hide destination');
    }
    
    if (url.includes('http://') && !url.includes('https://')) {
      urlRisk += 20;
      urlWarnings.push('Unsecured HTTP connection detected');
    }
    
    // Check for suspicious domains
    const suspiciousDomains = ['free-money', 'win-big', 'urgent-action', 'verify-now'];
    if (suspiciousDomains.some(domain => url.includes(domain))) {
      urlRisk += 40;
      urlWarnings.push('Suspicious domain pattern detected');
    }
  });
  
  return {
    urlRisk: Math.min(urlRisk, 100),
    warnings: urlWarnings,
    urlCount: urls.length
  };
};

// Language Pattern Analysis
const analyzeLanguagePatterns = (content) => {
  const urgencyWords = ['urgent', 'immediately', 'expires', 'limited', 'hurry', 'act now'];
  const emotionWords = ['amazing', 'incredible', 'shocking', 'unbelievable', 'miracle'];
  
  const words = content.toLowerCase().split(/\s+/);
  let urgencyScore = 0;
  let emotionScore = 0;
  
  words.forEach(word => {
    if (urgencyWords.some(uw => word.includes(uw))) urgencyScore += 10;
    if (emotionWords.some(ew => word.includes(ew))) emotionScore += 5;
  });
  
  return {
    urgencyScore: Math.min(urgencyScore, 100),
    emotionScore: Math.min(emotionScore, 100),
    wordCount: words.length
  };
};

// Fact-checking using free APIs (placeholder for real implementation)
const performFactCheck = async (content) => {
  // This would integrate with Google Fact Check Tools API or other free APIs
  // For now, return simulated results
  return {
    factCheckScore: Math.random() * 100,
    sources: Math.floor(Math.random() * 5) + 1,
    hasFactCheckData: Math.random() > 0.5
  };
};

// Combine all analysis results into final verdict
const combineAnalysisResults = (analyses, content, user) => {
  const { contentAnalysis, urlAnalysis, languageAnalysis, factCheck } = analyses;
  
  // Calculate overall scores
  const scamRisk = Math.round((contentAnalysis.scamScore + urlAnalysis.urlRisk + languageAnalysis.urgencyScore) / 3);
  const credibilityScore = Math.round(100 - ((contentAnalysis.misinfoScore + scamRisk + (100 - factCheck.factCheckScore)) / 3));
  
  // Determine verdict
  let verdict = 'Credible';
  if (credibilityScore < 40 || scamRisk > 70) verdict = 'High Risk';
  else if (credibilityScore < 70 || scamRisk > 40) verdict = 'Suspicious';
  
  // Compile warnings
  const warnings = [
    ...contentAnalysis.patterns,
    ...urlAnalysis.warnings
  ];
  
  // Generate recommendations
  const recommendations = [];
  if (scamRisk > 50) {
    recommendations.push('Do not click any links or provide personal information');
    recommendations.push('Verify sender through official channels');
  }
  if (credibilityScore < 60) {
    recommendations.push('Cross-check information with reliable sources');
    recommendations.push('Look for supporting evidence from multiple sources');
  }
  if (urlAnalysis.urlCount > 0) {
    recommendations.push('Hover over links to preview destinations before clicking');
  }
  
  recommendations.push('Report suspicious content to help protect others');
  
  return {
    credibilityScore,
    scamRisk,
    verdict,
    warnings,
    recommendations,
    summary: `Based on AI analysis of language patterns, content structure, and ${factCheck.sources} fact-checking sources, this content shows ${scamRisk}% scam risk indicators and ${credibilityScore}% credibility. Analysis performed for ${user.name}.`,
    analysisDate: new Date().toLocaleString(),
    confidence: Math.round((credibilityScore + (100 - scamRisk)) / 2)
  };
};

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (!isMobile) setSidebarOpen(false);
  }, [isMobile]);

  const menuItems = [
    { id: 'home', label: 'Home', icon: '🏠' },
    { id: 'verify', label: 'Verify Information', icon: '🛡️' },
    { id: 'education', label: 'Education', icon: '🎓' },
    { id: 'community', label: 'Community', icon: '👥' },
    { id: 'protection', label: 'Protection', icon: '🔒' },
    { id: 'analytics', label: 'Analytics', icon: '📊' },
    { id: 'authority', label: 'Authority', icon: '⚡' },
    { id: 'settings', label: 'Settings', icon: '⚙️' }
  ];

  const renderSection = () => {
    switch (currentSection) {
      case 'home':
  return (
    <div className="space-y-8">
      {/* Enhanced Hero Section */}
      <div className="relative overflow-hidden bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 rounded-2xl p-8 text-white text-center">
        <div className="absolute inset-0 opacity-20">
          <div className="absolute top-1/4 left-1/4 w-72 h-72 bg-cyan-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse"></div>
          <div className="absolute top-3/4 right-1/4 w-72 h-72 bg-purple-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse" style={{animationDelay: '2s'}}></div>
        </div>
        
        <div className="relative z-20 flex flex-col items-center">
          <div className="text-6xl mb-6 filter drop-shadow-lg"><img 
            src="/logo.png" 
            alt="Xist AI Logo" 
            className="h-20 w-20 hover:scale-110 transition-all duration-300"
            
          />
          <div className="absolute -inset-1  from-cyan-500 to-purple-600 rounded-xl blur opacity-30 hover:opacity-70 transition duration-300"></div></div>
          <h1 className="text-4xl md:text-6xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-white via-cyan-200 to-purple-200">
            Welcome to Xist AI
          </h1>
          <p className="text-2xl md:text-3xl opacity-95 mb-2 font-light text-cyan-100">
            Your Guardian Against Misinformation & Scams
          </p>
          <p className="text-lg opacity-80 mb-8 text-purple-200">
            Next-generation AI platform for comprehensive digital protection
          </p>
        </div>
      </div>

      {/* Main Action Tiles */}
      <div className="grid md:grid-cols-2 gap-8">
        {/* Launch Protection - Includes Google Sign-In Flow */}
        <button
          onClick={handleLaunchProtection}
          className="group relative p-8 rounded-2xl text-white font-bold text-xl shadow-2xl transform hover:scale-105 transition-all duration-500 bg-gradient-to-br from-purple-600 to-indigo-700 hover:shadow-purple-500/25 cursor-pointer"
        >
          <div className="text-6xl mb-4 transform group-hover:scale-110 transition-transform duration-300">
            {user ? '🚀' : '🔐'}
          </div>
          <div className="text-2xl font-bold mb-2">
            {user ? 'Launch Protection' : 'Sign In & Launch'}
          </div>
          <div className="text-sm opacity-80">
            {user 
              ? 'AI-powered security and threat detection' 
              : 'Sign in with Google to access protection'
            }
          </div>
        </button>

        {/* Verify Information - Enabled only after login */}
        <button
          onClick={handleVerifyInformation}
          disabled={!user}
          className={`group relative p-8 rounded-2xl text-white font-bold text-xl shadow-2xl transform hover:scale-105 transition-all duration-500 ${
            user 
              ? 'bg-gradient-to-br from-cyan-500 to-blue-600 hover:shadow-cyan-500/25 cursor-pointer' 
              : 'bg-gray-600 cursor-not-allowed opacity-60'
          }`}
        >
          <div className="text-6xl mb-4 transform group-hover:scale-110 transition-transform duration-300">🔍</div>
          <div className="text-2xl font-bold mb-2">Verify Information</div>
          <div className="text-sm opacity-80">Real-time fact-checking and analysis</div>
          {!user && (
            <div className="absolute inset-0 bg-black/40 rounded-2xl flex items-center justify-center">
              <span className="text-white font-semibold">Sign in first</span>
            </div>
          )}
        </button>
      </div>

      {/* User Info Section (shown after login) */}
      {user && (
        <div className="flex justify-center">
          <div className="flex items-center space-x-4 px-6 py-3 bg-white/10 backdrop-blur-sm border border-white/20 rounded-2xl text-white">
            <img src={user.picture} alt="Profile" className="w-12 h-12 rounded-full" />
            <div>
              <div className="font-semibold">Welcome back, {user.name}!</div>
              <div className="text-sm opacity-80">All features unlocked</div>
            </div>
            <button 
              onClick={() => setUser(null)}
              className="ml-4 px-3 py-1 bg-red-500/20 hover:bg-red-500/30 rounded-lg text-sm transition-all duration-300"
            >
              Sign Out
            </button>
          </div>
        </div>
      )}
    </div>
  );



      case 'verify':
  return (
    <div className="max-w-4xl mx-auto space-y-8">
      <div className="text-center">
        <div className="text-6xl mb-6">🔍</div>
        <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
          AI-Powered Information Verification
        </h1>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          Submit text, URLs, or claims for real-time AI analysis against misinformation and scams
        </p>
      </div>

      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
        <div className="space-y-6">
          <div>
            <label htmlFor="verify-input" className="block text-sm font-medium text-gray-700 mb-2">
              Enter text, URL, or claim to verify
            </label>
            <textarea
              id="verify-input"
              value={verifyInput}
              onChange={(e) => setVerifyInput(e.target.value)}
              placeholder="Paste the information, URL, or claim you want to verify here... 

Examples:
• 'Drinking lemon water cures cancer'
• 'You've won $10,000! Click here to claim'  
• 'https://suspicious-website.com/offer'"
              rows="6"
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 resize-none text-base"
            />
          </div>

          <div className="flex flex-col sm:flex-row gap-4">
            <button
              onClick={analyzeContent}
              disabled={isAnalyzing || !verifyInput.trim() || !user}
              className={`flex-1 px-6 py-3 rounded-lg font-semibold transition-all duration-200 flex items-center justify-center space-x-2 min-h-[48px] ${
                (!user) ? 'bg-gray-300 text-gray-500 cursor-not-allowed' :
                isAnalyzing ? 'bg-purple-400 text-white cursor-wait' :
                'bg-purple-600 hover:bg-purple-700 text-white hover:scale-105 transform'
              }`}
            >
              {isAnalyzing ? (
                <>
                  <div className="animate-spin h-5 w-5 border-2 border-white border-t-transparent rounded-full"></div>
                  <span>Analyzing with AI...</span>
                </>
              ) : !user ? (
                <>
                  <span className="text-xl">🔐</span>
                  <span>Sign in to Analyze</span>
                </>
              ) : (
                <>
                  <span className="text-xl">🤖</span>
                  <span>Analyze with AI</span>
                </>
              )}
            </button>

            <button className="flex items-center justify-center space-x-2 px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-all duration-200 min-h-[48px]">
              <span className="text-xl">📁</span>
              <span>Upload File</span>
            </button>
          </div>
        </div>
      </div>

      {analysisResult && (
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
            <span className="text-2xl mr-3">🛡️</span>
            AI Analysis Results
          </h3>
          
          <div className="grid md:grid-cols-3 gap-6 mb-6">
            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <div className={`text-4xl font-bold mb-2 ${
                analysisResult.credibilityScore > 70 ? 'text-green-600' : 
                analysisResult.credibilityScore > 40 ? 'text-yellow-600' : 'text-red-600'
              }`}>
                {analysisResult.credibilityScore}%
              </div>
              <div className="text-sm text-gray-600">Credibility Score</div>
            </div>

            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <div className={`text-4xl font-bold mb-2 ${
                analysisResult.scamRisk < 30 ? 'text-green-600' : 
                analysisResult.scamRisk < 70 ? 'text-yellow-600' : 'text-red-600'
              }`}>
                {analysisResult.scamRisk}%
              </div>
              <div className="text-sm text-gray-600">Scam Risk</div>
            </div>

            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <div className="text-2xl mb-2">
                {analysisResult.verdict === 'Credible' ? '✅' : 
                 analysisResult.verdict === 'Suspicious' ? '⚠️' : '❌'}
              </div>
              <div className={`font-semibold ${
                analysisResult.verdict === 'Credible' ? 'text-green-600' : 
                analysisResult.verdict === 'Suspicious' ? 'text-yellow-600' : 'text-red-600'
              }`}>
                {analysisResult.verdict}
              </div>
            </div>
          </div>

          <div className="space-y-4">
            <div className="p-4 bg-blue-50 rounded-lg">
              <h4 className="font-semibold text-blue-900 mb-2 flex items-center">
                <span className="text-xl mr-2">🧠</span>
                AI Analysis Summary
              </h4>
              <p className="text-blue-800 text-sm">
                {analysisResult.summary}
              </p>
            </div>

            {analysisResult.warnings && analysisResult.warnings.length > 0 && (
              <div className="p-4 bg-red-50 rounded-lg">
                <h4 className="font-semibold text-red-900 mb-2 flex items-center">
                  <span className="text-xl mr-2">⚠️</span>
                  Detected Warning Signs
                </h4>
                <ul className="text-red-800 text-sm space-y-1">
                  {analysisResult.warnings.map((warning, index) => (
                    <li key={index} className="flex items-start">
                      <span className="mr-2">•</span>
                      <span>{warning}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}

            <div className="p-4 bg-green-50 rounded-lg">
              <h4 className="font-semibold text-green-900 mb-2 flex items-center">
                <span className="text-xl mr-2">💡</span>
                Recommended Actions
              </h4>
              <ul className="text-green-800 text-sm space-y-1">
                {analysisResult.recommendations?.map((rec, index) => (
                  <li key={index} className="flex items-start">
                    <span className="mr-2">•</span>
                    <span>{rec}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      )}
    </div>
  );


      case 'education':
        return (
          <div className="space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-4">🎓</div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">Education & Training</h1>
<p className="text-xl text-gray-600">Master the art of detecting scams and misinformation</p>
            </div>
            
            <div className="grid md:grid-cols-2 gap-6">
              {[
                { title: 'AI Security Fundamentals', desc: 'Learn AI-powered security basics', duration: '4 hours' },
                { title: 'Threat Detection Methods', desc: 'Advanced detection techniques', duration: '6 hours' }
              ].map((course, index) => (
                <div key={index} className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
                  <h3 className="text-xl font-bold mb-2">{course.title}</h3>
                  <p className="text-gray-600 mb-4">{course.desc}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-500">{course.duration}</span>
                    <button className="bg-primary-600 text-white px-4 py-2 rounded">Start Course</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      default:
        return (
          <div className="text-center py-16">
            <div className="text-6xl mb-4">🚧</div>
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Coming Soon</h2>
            <p className="text-gray-600">This section is under development</p>
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      {/* 🚀 NEW INNOVATIVE HEADER */}
<header className="bg-gradient-to-r from-slate-900 via-purple-900 to-slate-900 shadow-2xl sticky top-0 z-50 border-b border-cyan-500/20">
  <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
    <div className="flex items-center justify-between h-16">
      <div className="flex items-center space-x-4">
        <div className="relative">
          <img 
            src="/logo.png" 
            alt="Xist AI Logo" 
            className="h-10 w-10 hover:scale-110 transition-all duration-300"
            style={{filter: 'drop-shadow(0 0 8px rgba(34, 211, 238, 0.6))'}}
          />
          <div className="absolute -inset-1  from-cyan-500 to-purple-600 rounded-xl blur opacity-30 hover:opacity-70 transition duration-300"></div>
        </div>
        <h1 className="text-2xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 to-purple-400 tracking-wide">
          Xist AI
        </h1>
      </div>

      {/* Mobile menu button */}
      {isMobile && (
        <button
          onClick={() => setSidebarOpen(!sidebarOpen)}
          className="relative p-3 rounded-xl bg-gradient-to-r from-cyan-500/20 to-purple-600/20 border border-cyan-500/30 hover:border-cyan-400/50 transition-all duration-300"
        >
          <div className="text-cyan-400 text-2xl">
            {sidebarOpen ? '✕' : '☰'}
          </div>
        </button>
      )}

      {/* Desktop navigation */}
      {!isMobile && (
        <nav className="flex space-x-6">
          {menuItems.slice(0, 4).map((item) => (
            <button
              key={item.id}
              onClick={() => setCurrentSection(item.id)}
              className={`text-white hover:text-cyan-300 px-3 py-2 rounded-md text-sm font-medium transition-all duration-300 ${
                currentSection === item.id ? 'bg-purple-700/50 text-cyan-300' : ''
              }`}
            >
              {item.label}
            </button>
          ))}
        </nav>
      )}
    </div>
  </div>
</header>
{/* END OF NEW HEADER */}



      <div className="flex">
        {/* 🌟 ENHANCED MOBILE SIDEBAR */}
{isMobile && sidebarOpen && (
  <>
    <div
      className="fixed inset-0 bg-black/70 backdrop-blur-sm z-40"
      onClick={() => setSidebarOpen(false)}
    />
    <aside className="fixed left-0 top-16 h-screen w-80 bg-gradient-to-b from-slate-900 to-purple-900/90 backdrop-blur-xl shadow-2xl z-50 overflow-y-auto border-r border-cyan-500/30">
      <div className="p-6">
        <div className="flex items-center justify-between mb-8">
          <h2 className="text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 to-purple-400">Navigation</h2>
          <button
            onClick={() => setSidebarOpen(false)}
            className="p-2 rounded-lg bg-slate-800/50 text-cyan-400 hover:bg-slate-700/50 transition-all duration-300"
          >
            <span className="text-xl">✕</span>
          </button>
        </div>
        
        <nav className="space-y-3">
          {menuItems.map((item, index) => (
            <button
              key={item.id}
              onClick={() => {
                setCurrentSection(item.id);
                setSidebarOpen(false);
              }}
              className={`w-full flex items-center space-x-4 px-4 py-4 text-left rounded-xl transition-all duration-300 group relative overflow-hidden ${
                currentSection === item.id
                  ? 'bg-gradient-to-r from-cyan-500/20 to-purple-600/20 text-cyan-300 border border-cyan-500/40'
                  : 'text-slate-300 hover:bg-slate-800/30 hover:text-cyan-400'
              }`}
            >
              {/* Glow effect on active */}
              {currentSection === item.id && (
                <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/10 to-purple-600/10 blur-sm"></div>
              )}
              <span className="text-2xl relative z-10 group-hover:scale-110 transition-transform duration-300">{item.icon}</span>
              <span className="font-medium relative z-10">{item.label}</span>
            </button>
          ))}
        </nav>
      </div>
    </aside>
  </>
)}

{/* 🌟 ENHANCED DESKTOP SIDEBAR */}
{!isMobile && (
  <aside className="w-80 bg-gradient-to-b from-slate-900 to-purple-900/90 backdrop-blur-xl shadow-2xl h-screen overflow-y-auto border-r border-cyan-500/20 fixed left-0 top-16">
    <div className="p-6">
      <div className="mb-8">
        <h2 className="text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 to-purple-400 mb-2">Navigation</h2>
        <p className="text-sm text-slate-400">AI-powered digital protection</p>
      </div>
      
      <nav className="space-y-3">
        {menuItems.map((item, index) => (
          <button
            key={item.id}
            onClick={() => setCurrentSection(item.id)}
            className={`w-full flex items-center space-x-4 px-4 py-4 text-left rounded-xl transition-all duration-300 group relative overflow-hidden ${
              currentSection === item.id
                ? 'bg-gradient-to-r from-cyan-500/20 to-purple-600/20 text-cyan-300 border border-cyan-500/40 shadow-lg shadow-cyan-500/10'
                : 'text-slate-300 hover:bg-slate-800/30 hover:text-cyan-400'
            }`}
          >
            {/* Glow effect on active */}
            {currentSection === item.id && (
              <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/10 to-purple-600/10 blur-sm"></div>
            )}
            <span className="text-2xl relative z-10 group-hover:scale-110 transition-transform duration-300">{item.icon}</span>
            <span className="font-medium relative z-10">{item.label}</span>
          </button>
        ))}
      </nav>
    </div>
  </aside>
)}


        {/* Main Content */}
        {/* 🌟 ENHANCED MAIN CONTENT */}
<main className={`flex-1 ${!isMobile ? 'ml-80' : ''} transition-all duration-300`}>
  <div className="p-4 sm:p-6 lg:p-8 min-h-screen bg-gradient-to-br from-slate-50 to-purple-50/30">
    <div className="max-w-7xl mx-auto">
      {renderSection()}
    </div>
  </div>
</main>

      </div>
    </div>
  );
};

export default App;
