import React, { useState, useEffect } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useGoogleLogin, googleLogout } from '@react-oauth/google'; 

const App = () => {
  const [user, setUser] = useState(null);

// Google Login handler using useGoogleLogin hook
const login = useGoogleLogin({
  onSuccess: async (tokenResponse) => {
    try {
      // Get user info from Google using the access token
      const response = await fetch('https://www.googleapis.com/oauth2/v3/userinfo', {
        headers: {
          Authorization: `Bearer ${tokenResponse.access_token}`,
        },
      });
      const userInfo = await response.json();
      setUser(userInfo);
      console.log('Login successful:', userInfo);
      
      // Automatically launch protection after successful login
      alert(`Welcome ${userInfo.name}! üõ°Ô∏è Launching AI Protection Features...`);
      // Add your real protection logic here
    } catch (error) {
      console.error('Error fetching user info:', error);
      alert('Login successful but could not fetch user details');
    }
  },
  onError: () => {
    console.error('Google Sign-In failed');
    alert('Google Sign-In failed. Please try again.');
  }
});

// Handle Launch Protection button click
const handleLaunchProtection = () => {
  if (user) {
    // User already logged in, proceed directly
    alert(`Welcome back ${user.name}! üöÄ Launching Protection Features...`);
    // Add your real protection logic here
  } else {
    // User not logged in, trigger Google Sign-In
    login();
  }
};

// Handle Verify button (requires login)
const handleVerifyInformation = () => {
  if (!user) {
    alert('Please sign in first by clicking Launch Protection!');
    return;
  }
  alert(`Welcome ${user.name}! üîç Opening Verification Tools...`);
  // Add your real verification logic here
};
  const [currentSection, setCurrentSection] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [verifyInput, setVerifyInput] = useState('');
const [isAnalyzing, setIsAnalyzing] = useState(false);
const [analysisResult, setAnalysisResult] = useState(null);

const analyzeContent = async () => {
  if (!verifyInput.trim() || !user) return;
  
  setIsAnalyzing(true);
  setAnalysisResult(null);
  
  try {
    // Multi-layered analysis combining different techniques
    const analysis = await performAIAnalysis(verifyInput, user);
    setAnalysisResult(analysis);
  } catch (error) {
    console.error('Analysis error:', error);
    alert('Analysis failed. Please try again.');
  } finally {
    setIsAnalyzing(false);
  }
};

// Advanced AI Analysis Function - FREE implementation
// Enhanced API-based analysis
const performAIAnalysis = async (content, user) => {
  try {
    const response = await fetch('http://localhost:5000/api/analyze', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        content: content,
        user_email: user.email,
        user_name: user.name
      })
    });
    
    if (!response.ok) {
      throw new Error('Analysis failed');
    }
    
    const result = await response.json();
    return result;
    
  } catch (error) {
    console.error('API Error:', error);
    // Fallback to local analysis if API is down
    return await performLocalAnalysis(content, user);
  }
};

// Keep your existing local analysis as fallback
const performLocalAnalysis = async (content, user) => {
  // Your existing local analysis code here
  // This ensures your app works even if backend is offline
};

const UserAnalytics = ({ user }) => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserStats();
  }, [user.email]);

  const fetchUserStats = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/user/stats/${user.email}`);
      const data = await response.json();
      setStats(data);
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin h-12 w-12 border-4 border-purple-500 border-t-transparent rounded-full"></div>
      </div>
    );
  }

  if (!stats) {
    return (
      <div className="bg-white rounded-xl p-8 text-center">
        <div className="text-4xl mb-4">üìä</div>
        <h3 className="text-xl font-semibold mb-2">No Analysis Data Yet</h3>
        <p className="text-gray-600">Start analyzing content to see your protection statistics!</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Stats Overview */}
      <div className="grid md:grid-cols-4 gap-6">
        <div className="bg-white rounded-xl p-6 text-center shadow-sm">
          <div className="text-3xl font-bold text-blue-600 mb-2">{stats.totalAnalyses}</div>
          <div className="text-sm text-gray-600">Total Analyses</div>
        </div>
        <div className="bg-white rounded-xl p-6 text-center shadow-sm">
          <div className="text-3xl font-bold text-green-600 mb-2">{Math.round(stats.averageCredibility)}%</div>
          <div className="text-sm text-gray-600">Avg Credibility</div>
        </div>
        <div className="bg-white rounded-xl p-6 text-center shadow-sm">
          <div className="text-3xl font-bold text-red-600 mb-2">{Math.round(stats.averageScamRisk)}%</div>
          <div className="text-sm text-gray-600">Avg Scam Risk</div>
        </div>
        <div className="bg-white rounded-xl p-6 text-center shadow-sm">
          <div className="text-3xl font-bold text-purple-600 mb-2">üõ°Ô∏è</div>
          <div className="text-sm text-gray-600">Protected</div>
        </div>
      </div>

      {/* Recent Analyses */}
      <div className="bg-white rounded-xl p-6 shadow-sm">
        <h3 className="text-xl font-semibold mb-4">Recent Analysis History</h3>
        <div className="space-y-3">
          {stats.recentAnalyses?.map((analysis, index) => (
            <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
              <div className="flex items-center space-x-3">
                <div className={`text-2xl ${
                  analysis.verdict === 'Credible' ? '‚úÖ' : 
                  analysis.verdict === 'Suspicious' ? '‚ö†Ô∏è' : '‚ùå'
                }`}></div>
                <div>
                  <div className="font-medium">{analysis.verdict}</div>
                  <div className="text-sm text-gray-600">
                    {new Date(analysis.date).toLocaleDateString()}
                  </div>
                </div>
              </div>
              <div className="text-right">
                <div className="text-sm">
                  <span className="text-green-600">{analysis.credibilityScore}% credible</span>
                </div>
                <div className="text-sm">
                  <span className="text-red-600">{analysis.scamRisk}% scam risk</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};


// Content Pattern Analysis (Detects scam indicators)
const analyzeContentPatterns = (content) => {
  const scamPatterns = [
    /urgent.*action.*required/i,
    /click.*here.*immediately/i,
    /you.*won.*\$[\d,]+/i,
    /limited.*time.*offer/i,
    /verify.*account.*suspended/i,
    /congratulations.*winner/i,
    /act.*now.*expires/i
  ];
  
  const suspiciousPhrases = [
    /miracle.*cure/i,
    /doctors.*hate.*this/i,
    /100%.*guaranteed/i,
    /secret.*government/i,
    /they.*don't.*want.*you.*to.*know/i
  ];
  
  let scamScore = 0;
  let misinfoScore = 0;
  let detectedPatterns = [];
  
  scamPatterns.forEach(pattern => {
    if (pattern.test(content)) {
      scamScore += 25;
      detectedPatterns.push('Scam language pattern detected');
    }
  });
  
  suspiciousPhrases.forEach(pattern => {
    if (pattern.test(content)) {
      misinfoScore += 20;
      detectedPatterns.push('Suspicious claim pattern detected');
    }
  });
  
  return {
    scamScore: Math.min(scamScore, 100),
    misinfoScore: Math.min(misinfoScore, 100),
    patterns: detectedPatterns
  };
};

// URL Analysis Function
const analyzeUrls = (content) => {
  const urlRegex = /https?:\/\/[^\s]+/g;
  const urls = content.match(urlRegex) || [];
  
  let urlRisk = 0;
  let urlWarnings = [];
  
  urls.forEach(url => {
    // Check for suspicious URL patterns
    if (url.includes('bit.ly') || url.includes('tinyurl') || url.includes('t.co')) {
      urlRisk += 30;
      urlWarnings.push('Shortened URL detected - could hide destination');
    }
    
    if (url.includes('http://') && !url.includes('https://')) {
      urlRisk += 20;
      urlWarnings.push('Unsecured HTTP connection detected');
    }
    
    // Check for suspicious domains
    const suspiciousDomains = ['free-money', 'win-big', 'urgent-action', 'verify-now'];
    if (suspiciousDomains.some(domain => url.includes(domain))) {
      urlRisk += 40;
      urlWarnings.push('Suspicious domain pattern detected');
    }
  });
  
  return {
    urlRisk: Math.min(urlRisk, 100),
    warnings: urlWarnings,
    urlCount: urls.length
  };
};

// Language Pattern Analysis
const analyzeLanguagePatterns = (content) => {
  const urgencyWords = ['urgent', 'immediately', 'expires', 'limited', 'hurry', 'act now'];
  const emotionWords = ['amazing', 'incredible', 'shocking', 'unbelievable', 'miracle'];
  
  const words = content.toLowerCase().split(/\s+/);
  let urgencyScore = 0;
  let emotionScore = 0;
  
  words.forEach(word => {
    if (urgencyWords.some(uw => word.includes(uw))) urgencyScore += 10;
    if (emotionWords.some(ew => word.includes(ew))) emotionScore += 5;
  });
  
  return {
    urgencyScore: Math.min(urgencyScore, 100),
    emotionScore: Math.min(emotionScore, 100),
    wordCount: words.length
  };
};

// Fact-checking using free APIs (placeholder for real implementation)
const performFactCheck = async (content) => {
  // This would integrate with Google Fact Check Tools API or other free APIs
  // For now, return simulated results
  return {
    factCheckScore: Math.random() * 100,
    sources: Math.floor(Math.random() * 5) + 1,
    hasFactCheckData: Math.random() > 0.5
  };
};

// Combine all analysis results into final verdict
const combineAnalysisResults = (analyses, content, user) => {
  const { contentAnalysis, urlAnalysis, languageAnalysis, factCheck } = analyses;
  
  // Calculate overall scores
  const scamRisk = Math.round((contentAnalysis.scamScore + urlAnalysis.urlRisk + languageAnalysis.urgencyScore) / 3);
  const credibilityScore = Math.round(100 - ((contentAnalysis.misinfoScore + scamRisk + (100 - factCheck.factCheckScore)) / 3));
  
  // Determine verdict
  let verdict = 'Credible';
  if (credibilityScore < 40 || scamRisk > 70) verdict = 'High Risk';
  else if (credibilityScore < 70 || scamRisk > 40) verdict = 'Suspicious';
  
  // Compile warnings
  const warnings = [
    ...contentAnalysis.patterns,
    ...urlAnalysis.warnings
  ];
  
  // Generate recommendations
  const recommendations = [];
  if (scamRisk > 50) {
    recommendations.push('Do not click any links or provide personal information');
    recommendations.push('Verify sender through official channels');
  }
  if (credibilityScore < 60) {
    recommendations.push('Cross-check information with reliable sources');
    recommendations.push('Look for supporting evidence from multiple sources');
  }
  if (urlAnalysis.urlCount > 0) {
    recommendations.push('Hover over links to preview destinations before clicking');
  }
  
  recommendations.push('Report suspicious content to help protect others');
  
  return {
    credibilityScore,
    scamRisk,
    verdict,
