      const errorMessage = {
        sender: 'bot',
        message: `Hello ${user.name}! I'm currently in ${!isOnline ? 'offline' : 'fallback'} mode. For full AI responses, ensure you're connected and have configured your OpenRouter API key in settings. I can still help with basic digital safety guidance!`,
        timestamp: new Date().toISOString(),
        isAI: false
      };
      setChatMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsChatLoading(false);
      
      setTimeout(() => {
