import React, { useState, useEffect } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useGoogleLogin, googleLogout } from '@react-oauth/google'; 

const App = () => {
  const [user, setUser] = useState(null);

// Google Login handler using useGoogleLogin hook
const login = useGoogleLogin({
  onSuccess: async (tokenResponse) => {
    try {
      // Get user info from Google using the access token
      const response = await fetch('https://www.googleapis.com/oauth2/v3/userinfo', {
        headers: {
          Authorization: `Bearer ${tokenResponse.access_token}`,
        },
      });
      const userInfo = await response.json();
      setUser(userInfo);
      console.log('Login successful:', userInfo);
      
      // Automatically launch protection after successful login
      alert(`Welcome ${userInfo.name}! üõ°Ô∏è Launching AI Protection Features...`);
      // Add your real protection logic here
    } catch (error) {
      console.error('Error fetching user info:', error);
      alert('Login successful but could not fetch user details');
    }
  },
  onError: () => {
    console.error('Google Sign-In failed');
    alert('Google Sign-In failed. Please try again.');
  }
});

// Handle Launch Protection button click
const handleLaunchProtection = () => {
  if (user) {
    // User already logged in, proceed directly
    alert(`Welcome back ${user.name}! üöÄ Launching Protection Features...`);
    // Add your real protection logic here
  } else {
    // User not logged in, trigger Google Sign-In
    login();
  }
};

// Handle Verify button (requires login)
const handleVerifyInformation = () => {
  if (!user) {
    alert('Please sign in first by clicking Launch Protection!');
    return;
  }
  alert(`Welcome ${user.name}! üîç Opening Verification Tools...`);
  // Add your real verification logic here
};
  const [currentSection, setCurrentSection] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [verifyInput, setVerifyInput] = useState('');
const [isAnalyzing, setIsAnalyzing] = useState(false);
const [analysisResult, setAnalysisResult] = useState(null);



  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (!isMobile) setSidebarOpen(false);
  }, [isMobile]);

  const menuItems = [
    { id: 'home', label: 'Home', icon: 'üè†' },
    { id: 'verify', label: 'Verify Information', icon: 'üõ°Ô∏è' },
    { id: 'education', label: 'Education', icon: 'üéì' },
    { id: 'community', label: 'Community', icon: 'üë•' },
    { id: 'protection', label: 'Protection', icon: 'üîí' },
    { id: 'analytics', label: 'Analytics', icon: 'üìä' },
    { id: 'authority', label: 'Authority', icon: '‚ö°' },
    { id: 'settings', label: 'Settings', icon: '‚öôÔ∏è' }
  ];

  const renderSection = () => {
    switch (currentSection) {
      case 'home':
  return (
    <div className="space-y-8">
      {/* Enhanced Hero Section */}
      <div className="relative overflow-hidden bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 rounded-2xl p-8 text-white text-center">
        <div className="absolute inset-0 opacity-20">
          <div className="absolute top-1/4 left-1/4 w-72 h-72 bg-cyan-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse"></div>
          <div className="absolute top-3/4 right-1/4 w-72 h-72 bg-purple-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse" style={{animationDelay: '2s'}}></div>
        </div>
        
        <div className="relative z-20 flex flex-col items-center">
          <div className="text-6xl mb-6 filter drop-shadow-lg"><img 
            src="/logo.png" 
            alt="Xist AI Logo" 
            className="h-20 w-20 hover:scale-110 transition-all duration-300"
            
          />
          <div className="absolute -inset-1  from-cyan-500 to-purple-600 rounded-xl blur opacity-30 hover:opacity-70 transition duration-300"></div></div>
          <h1 className="text-4xl md:text-6xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-white via-cyan-200 to-purple-200">
            Welcome to Xist AI
          </h1>
          <p className="text-2xl md:text-3xl opacity-95 mb-2 font-light text-cyan-100">
            Your Guardian Against Misinformation & Scams
          </p>
          <p className="text-lg opacity-80 mb-8 text-purple-200">
            Next-generation AI platform for comprehensive digital protection
          </p>
        </div>
      </div>

