                {
                  name: 'Data Protection',
                  description: 'Encrypts and secures personal information',
                  status: protectionStatus.dataProtection,
                  icon: '🔒'
                },
                {
                  name: 'Instant Alerts',
                  description: 'Immediate notifications for threats',
