import React, { useState, useEffect } from 'react';
import { GoogleLogin } from '@react-oauth/google';

const App = () => {
  const [currentSection, setCurrentSection] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (!isMobile) setSidebarOpen(false);
  }, [isMobile]);

  const menuItems = [
    { id: 'home', label: 'Home', icon: '🏠' },
    { id: 'verify', label: 'Verify Information', icon: '🛡️' },
    { id: 'education', label: 'Education', icon: '🎓' },
    { id: 'community', label: 'Community', icon: '👥' },
    { id: 'protection', label: 'Protection', icon: '🔒' },
    { id: 'analytics', label: 'Analytics', icon: '📊' },
    { id: 'authority', label: 'Authority', icon: '⚡' },
    { id: 'settings', label: 'Settings', icon: '⚙️' }
  ];

  const renderSection = () => {
    switch (currentSection) {
      case 'home':
  return (
    <div className="space-y-8">
      {/* 🌟 ENHANCED HERO SECTION WITH GOOGLE LOGIN */}
      <div className="relative overflow-hidden bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 rounded-2xl p-8 text-white text-center">
        {/* Animated background blobs */}
        <div className="absolute inset-0 opacity-20">
          <div className="absolute top-1/4 left-1/4 w-72 h-72 bg-cyan-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse"></div>
          <div className="absolute top-3/4 right-1/4 w-72 h-72 bg-purple-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse" style={{animationDelay: '2s'}}></div>
          <div className="absolute bottom-1/4 left-1/2 w-72 h-72 bg-pink-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse" style={{animationDelay: '4s'}}></div>
        </div>
        
        {/* Content */}
        <div className="relative z-10">
          <div> 
                <img src="/logo.png" alt="Xist AI Logo" className="mx-auto h-20 w-20 mb-4 rounded-lg" />
              </div>
                <div className="absolute -inset-1  from-cyan-500 to-purple-600 rounded-xl blur opacity-30 hover:opacity-70 transition duration-300"></div>
          <h1 className="text-4xl md:text-6xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-white via-cyan-200 to-purple-200">
            Welcome to Xist AI
          </h1>
          <p className="text-2xl md:text-3xl opacity-95 mb-2 font-light text-cyan-100">
            Your Guardian Against Misinformation & Scams
          </p>
          <p className="text-lg opacity-80 mb-8 text-purple-200">
            Next-generation AI platform for comprehensive digital protection
          </p>
          
          <div className="flex space-x-4 justify-center mt-6">
  <button
    onClick={handleProtection}
    disabled={!user}
    className={`flex items-center space-x-2 px-6 py-3 rounded-lg font-semibold transition-colors duration-300
      ${user ? 'bg-gradient-to-r from-purple-600 to-indigo-700 hover:from-purple-700 hover:to-indigo-800 text-white' : 'bg-gray-500 cursor-not-allowed text-gray-300'}`}
  >
    <span>🔒</span>
    <span>Launch Protection</span>
  </button>

  {!user ? (
    <GoogleLogin
      onSuccess={handleLoginSuccess}
      onError={handleLoginError}
      useOneTap={true}
      auto_select={true}
      size="large"
      shape="rectangular"
    />
  ) : (
    <div className="flex items-center space-x-2 text-white font-medium">
      {user.picture && <img src={user.picture} alt="profile" className="w-8 h-8 rounded-full" />}
      <span>Hello, {user.name}</span>
    </div>
  )}
</div>

        </div>
      </div>

      {/* Enhanced Feature Cards */}
      <div className="grid md:grid-cols-3 gap-8">
        {[
          { 
            icon: '🛡️', 
            title: 'Scam Shield', 
            desc: 'AI-powered detection of fraudulent schemes and malicious content',
            gradient: 'from-red-500 to-pink-600'
          },
          { 
            icon: '🔍', 
            title: 'Truth Radar', 
            desc: 'Real-time fact-checking and misinformation identification',
            gradient: 'from-cyan-500 to-blue-600'
          },
          { 
            icon: '📊', 
            title: 'Smart Analytics', 
            desc: 'Instant assessment of digital content credibility and safety',
            gradient: 'from-purple-500 to-indigo-600'
          }
        ].map((feature, index) => (
          <div 
            key={index} 
            className="group relative bg-slate-900/50 backdrop-blur-xl rounded-2xl p-8 shadow-2xl border border-slate-700/50 hover:border-cyan-500/50 transition-all duration-500 hover:scale-105 cursor-pointer"
          >
            {/* Glow effect */}
            <div className={`absolute -inset-0.5 bg-gradient-to-r ${feature.gradient} rounded-2xl blur opacity-0 group-hover:opacity-30 transition duration-500`}></div>
            
            <div className="relative">
              <div className="text-5xl mb-6 transform group-hover:scale-110 transition-transform duration-300 filter drop-shadow-lg">
                {feature.icon}
              </div>
              <h3 className={`text-xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r ${feature.gradient}`}>
                {feature.title}
              </h3>
              <p className="text-slate-300 leading-relaxed">
                {feature.desc}
              </p>
              
              {/* Hover indicator */}
              <div className={`mt-6 w-12 h-1 bg-gradient-to-r ${feature.gradient} rounded-full transform scale-x-0 group-hover:scale-x-100 transition-transform duration-300`}></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

      case 'verify':
        return (
          <div className="max-w-4xl mx-auto space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-4">🔍</div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">Information Verification</h1>
<p className="text-xl text-gray-600 mb-2">Protect yourself from misinformation and scams</p>
<p className="text-lg text-gray-500">AI-powered analysis for digital safety</p>
            </div>
            
            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
              <textarea
                placeholder="Enter text to verify..."
                rows="6"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 resize-none"
              />
              <button className="mt-6 bg-gradient-to-r from-primary-600 to-primary-700 hover:from-primary-700 hover:to-primary-800 text-white px-8 py-4 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300">
  🔍 Verify Information
</button>
            </div>
          </div>
        );

      case 'education':
        return (
          <div className="space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-4">🎓</div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">Education & Training</h1>
<p className="text-xl text-gray-600">Master the art of detecting scams and misinformation</p>
            </div>
            
            <div className="grid md:grid-cols-2 gap-6">
              {[
                { title: 'AI Security Fundamentals', desc: 'Learn AI-powered security basics', duration: '4 hours' },
                { title: 'Threat Detection Methods', desc: 'Advanced detection techniques', duration: '6 hours' }
              ].map((course, index) => (
                <div key={index} className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
                  <h3 className="text-xl font-bold mb-2">{course.title}</h3>
                  <p className="text-gray-600 mb-4">{course.desc}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-500">{course.duration}</span>
                    <button className="bg-primary-600 text-white px-4 py-2 rounded">Start Course</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      default:
        return (
          <div className="text-center py-16">
            <div className="text-6xl mb-4">🚧</div>
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Coming Soon</h2>
            <p className="text-gray-600">This section is under development</p>
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      {/* 🚀 NEW INNOVATIVE HEADER */}
<header className="bg-gradient-to-r from-slate-900 via-purple-900 to-slate-900 shadow-2xl sticky top-0 z-50 border-b border-cyan-500/20">
  <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
    <div className="flex items-center justify-between h-16">
      <div className="flex items-center space-x-4">
        <div className="relative">
          <img 
            src="/logo.png" 
            alt="Xist AI Logo" 
            className="h-10 w-10 hover:scale-110 transition-all duration-300"
            style={{filter: 'drop-shadow(0 0 8px rgba(34, 211, 238, 0.6))'}}
          />
          <div className="absolute -inset-1  from-cyan-500 to-purple-600 rounded-xl blur opacity-30 hover:opacity-70 transition duration-300"></div>
        </div>
        <h1 className="text-2xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 to-purple-400 tracking-wide">
          Xist AI
        </h1>
      </div>

      {/* Mobile menu button */}
      {isMobile && (
        <button
          onClick={() => setSidebarOpen(!sidebarOpen)}
          className="relative p-3 rounded-xl bg-gradient-to-r from-cyan-500/20 to-purple-600/20 border border-cyan-500/30 hover:border-cyan-400/50 transition-all duration-300"
        >
          <div className="text-cyan-400 text-2xl">
            {sidebarOpen ? '✕' : '☰'}
          </div>
        </button>
      )}

      {/* Desktop navigation */}
      {!isMobile && (
        <nav className="flex space-x-6">
          {menuItems.slice(0, 4).map((item) => (
            <button
              key={item.id}
              onClick={() => setCurrentSection(item.id)}
              className={`text-white hover:text-cyan-300 px-3 py-2 rounded-md text-sm font-medium transition-all duration-300 ${
                currentSection === item.id ? 'bg-purple-700/50 text-cyan-300' : ''
              }`}
            >
              {item.label}
            </button>
          ))}
        </nav>
      )}
    </div>
  </div>
</header>
{/* END OF NEW HEADER */}



      <div className="flex">
        {/* 🌟 ENHANCED MOBILE SIDEBAR */}
{isMobile && sidebarOpen && (
  <>
    <div
      className="fixed inset-0 bg-black/70 backdrop-blur-sm z-40"
      onClick={() => setSidebarOpen(false)}
    />
    <aside className="fixed left-0 top-16 h-screen w-80 bg-gradient-to-b from-slate-900 to-purple-900/90 backdrop-blur-xl shadow-2xl z-50 overflow-y-auto border-r border-cyan-500/30">
      <div className="p-6">
        <div className="flex items-center justify-between mb-8">
          <h2 className="text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 to-purple-400">Navigation</h2>
          <button
            onClick={() => setSidebarOpen(false)}
            className="p-2 rounded-lg bg-slate-800/50 text-cyan-400 hover:bg-slate-700/50 transition-all duration-300"
          >
            <span className="text-xl">✕</span>
          </button>
        </div>
        
        <nav className="space-y-3">
          {menuItems.map((item, index) => (
            <button
              key={item.id}
              onClick={() => {
                setCurrentSection(item.id);
                setSidebarOpen(false);
              }}
              className={`w-full flex items-center space-x-4 px-4 py-4 text-left rounded-xl transition-all duration-300 group relative overflow-hidden ${
                currentSection === item.id
                  ? 'bg-gradient-to-r from-cyan-500/20 to-purple-600/20 text-cyan-300 border border-cyan-500/40'
                  : 'text-slate-300 hover:bg-slate-800/30 hover:text-cyan-400'
              }`}
            >
              {/* Glow effect on active */}
              {currentSection === item.id && (
                <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/10 to-purple-600/10 blur-sm"></div>
              )}
              <span className="text-2xl relative z-10 group-hover:scale-110 transition-transform duration-300">{item.icon}</span>
              <span className="font-medium relative z-10">{item.label}</span>
            </button>
          ))}
        </nav>
      </div>
    </aside>
  </>
)}

{/* 🌟 ENHANCED DESKTOP SIDEBAR */}
{!isMobile && (
  <aside className="w-80 bg-gradient-to-b from-slate-900 to-purple-900/90 backdrop-blur-xl shadow-2xl h-screen overflow-y-auto border-r border-cyan-500/20 fixed left-0 top-16">
    <div className="p-6">
      <div className="mb-8">
        <h2 className="text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 to-purple-400 mb-2">Navigation</h2>
        <p className="text-sm text-slate-400">AI-powered digital protection</p>
      </div>
      
      <nav className="space-y-3">
        {menuItems.map((item, index) => (
          <button
            key={item.id}
            onClick={() => setCurrentSection(item.id)}
            className={`w-full flex items-center space-x-4 px-4 py-4 text-left rounded-xl transition-all duration-300 group relative overflow-hidden ${
              currentSection === item.id
                ? 'bg-gradient-to-r from-cyan-500/20 to-purple-600/20 text-cyan-300 border border-cyan-500/40 shadow-lg shadow-cyan-500/10'
                : 'text-slate-300 hover:bg-slate-800/30 hover:text-cyan-400'
            }`}
          >
            {/* Glow effect on active */}
            {currentSection === item.id && (
              <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/10 to-purple-600/10 blur-sm"></div>
            )}
            <span className="text-2xl relative z-10 group-hover:scale-110 transition-transform duration-300">{item.icon}</span>
            <span className="font-medium relative z-10">{item.label}</span>
          </button>
        ))}
      </nav>
    </div>
  </aside>
)}


        {/* Main Content */}
        {/* 🌟 ENHANCED MAIN CONTENT */}
<main className={`flex-1 ${!isMobile ? 'ml-80' : ''} transition-all duration-300`}>
  <div className="p-4 sm:p-6 lg:p-8 min-h-screen bg-gradient-to-br from-slate-50 to-purple-50/30">
    <div className="max-w-7xl mx-auto">
      {renderSection()}
    </div>
  </div>
</main>

      </div>
    </div>
  );
};

export default App;
